import Layout from '../components/MyLayout';
import axios from 'axios';
import styled from 'styled-components';

const Form = styled.form`
  display: flex;
  flex-direction: column;

  &__item {
    margin-bottom: 16px;

    &:last-of-type {
      margin-bottom: 32px;
    }
  }

  &__input {
    box-sizing: border-box;
    font-family: "Roboto", sans-serif;
    font-size: 14px;
    line-height: 14px;
    color: #393939;
    padding: 0 17px;
    width: 100%;
    height: 46px;
    background: #f7f8ff;
    border: 1px solid #fff;
    border-radius: 8px;
    outline: none;
    @include hover(border-color, #00AAEF);
    @include hover(border-width, 2px);
  }

  &__button {
    font-family: "Roboto", sans-serif;
    width: 100%;
    height: 56px;
    font-size: 16px;
    line-height: 14px;
    font-weight: 700;
    background: $color-blue;
    box-shadow: 0 6px 23px rgba(0, 117, 255, 0.46);
    border-radius: 8px;
    border-style: none;
    cursor: pointer;
    color: #fff;
    @include hover(background-color, #00AAEF);

  }

  &__input::placeholder {
    font-family: "Roboto", sans-serif;
    font-size: 14px;
    line-height: 14px;
    color: #b9c1ce;
  }
`;

const New = props => {

  return (
    <Layout>
      <h2 class="form__heading">New post</h2>
      <Form action="" class="form contacts__form">
        <label class="form__item">Title
          <input type="text" name="name" class="input form__input" placeholder="Name" />
        </label>
        <label class="form__item">Content
          <textarea name="message" class="input input_textarea form__input" placeholder="Message"></textarea>
        </label>
        <button class="button form__button">Add post</button>
      </Form>
    </Layout>
  );
}

export default New;
