import { useState } from 'react';
import Layout from '.././components/MyLayout';
import axios from 'axios';
import styled from 'styled-components';

const Button = styled.button`
  display: none;
  position: absolute;
  margin: 0;
  padding: 0;
  border: 0;  
  background: none;
  top: 10px;
  right: 20px;
  width: 30px;
  font-size: 40px;
  font-family: arial;
  color: #cc9a9a;
  outline: none;
  transition: color 0.2s ease-out;
  cursor: pointer;

  &:after {
    content: "×";
  }
`;

const Section = styled.section`
  position: relative;
  font-family: "Lucida Sans Unicode", "Lucida Grande", sans-serif;
  width: 1000px;
  padding: 20px;
  margin-top: 20px;
  border-radius: 10px;
  box-shadow: 0 0 17px -1px rgba(0,0,0,0.81);

  & .destroy-post {
    display: none;
    position: absolute;
    margin: 0;
    padding: 0;
    border: 0;  
    background: none;
    top: 10px;
    right: 20px;
    width: 30px;
    font-size: 40px;
    font-family: arial;
    color: #cc9a9a;
    outline: none;
    transition: color 0.2s ease-out;
    cursor: pointer;

    &:after {
      content: "×";
    }
  }

  &:hover .destroy-post {
    display: block;
  }

  & .destroy-post:hover {
    color: #af5b5e;
  }

  & .post__text {
    margin-bottom: 40px;
  }

  & .post__text:after {
    content: "";
    display: block;
    margin: 40px auto;
    width: 95%;
    border-bottom: 1px solid black;
  }
`;

const Comment = styled.div`
  position: relative;
  width: 950px;
  margin: 0 auto 15px;
  padding: 15px;
  background-color: #f2f3f5;
  border-radius: 18px;

  & .destroy-comment {
    display: none;
    position: absolute;
    margin: 0;
    padding: 0;
    border: 0;  
    background: none;
    top: 10px;
    right: 20px;
    width: 30px;
    font-size: 40px;
    font-family: arial;
    color: #cc9a9a;
    outline: none;
    transition: color 0.2s ease-out;
    cursor: pointer;

    &:after {
      content: "×";
    }
  }

  & .destroy-comment:hover {
    color: #af5b5e;
  }

  &:hover .destroy-comment {
    display: block;
  }
`;

const Form = styled.form`
  width: 100%;

  & .form__button {
    display: block;
    margin: 0 auto;
  }
`;

const Textarea = styled.textarea`
  display: block;
  margin: 10px auto;
  width: 380px;
  height: 80px;
  border-radius: 5px;
`;

const Post = props => {
  const [value, setValue] = useState('');

  console.log(props);

  const handleChange = (event) => {
    setValue(event.target.value);
  }

  const handleSubmit = async (event) => {
    event.preventDefault();

    if(!value) {
      return;
    }

    const response = await axios.post('https://simple-blog-api.crew.red/comments', {postId: props.show.comments[0].postId, body: value });

    if (response.data) {
      setValue('');
    }
  }

  const deleteComment = async (event) => {
    const { id } = event.target.dataset;
    await axios.delete(`https://simple-blog-api.crew.red/comments/${id}`);
  }

  return (
    <Layout>
      <Section>
        <button
          type="button"
          className="destroy-post"
          aria-label="Delete"
          data-name={props.show.id}
        />
        <h2 className="post__heading">{props.show.title}</h2>
        <p className="post__text">{props.show.body}</p>
        <div>
          {props.show.comments.map(comment => (
            <Comment key={comment.id}>
              <button
                type="button"
                className="destroy-comment"
                aria-label="Delete"
                data-id={comment.id}
                onClick={deleteComment}
              />
              <p className="comment__text">{comment.body}</p>
            </Comment>
          ))}
        </div>
        <Form onSubmit={handleSubmit}>
          <Textarea 
            type="text" 
            placeholder="comment"
            readonly
            value={value}
            onChange={handleChange} 
          />
          <button type="submit" className="form__button">Add comment</button>
        </Form>
      </Section>
      {/* <h1>{props.show.title}</h1>
      <p>{props.show.body}</p> */}
      {/* <p>{props.show.summary.replace(/<[/]?[pb]>/g, '')}</p>
      {props.show.image ? <img src={props.show.image.medium} /> : null} */}
    </Layout>
  )
};

Post.getInitialProps = async function(context) {
  const { id } = context.query;
  // const res = await fetch(`https://api.tvmaze.com/shows/${id}`);
  // const show = await res.json();
  const res = await axios.get(`https://simple-blog-api.crew.red/posts/${id}?_embed=comments`);
  const show = await res.data;

  return { show };
};

export default Post;
